import React, { useEffect, useState } from 'react';
import axios from '../services/api';
import Navbar from '../components/Navbar';
import RewardCard from '../components/RewardCard';

const Dashboard = () => {
  const [data, setData] = useState(null);

  useEffect(() => {
    axios.get('/user')
      .then(res => setData(res.data))
      .catch(err => console.error(err));
  }, []);

  return (
    <div className="min-h-screen bg-gray-50">
      <Navbar />
      <div className="p-6 max-w-xl mx-auto">
        {data ? (
          <>
            <h1 className="text-3xl font-bold mb-4">Welcome, {data.name}</h1>
            <p className="mb-2">Referral Code: <strong>{data.referralCode}</strong></p>
            <p className="mb-4">Total Raised: ‚Çπ{data.amountRaised}</p>

            <h2 className="text-xl font-semibold mb-2">üéÅ Rewards</h2>
            <div className="grid grid-cols-1 gap-4">
              <RewardCard title="‚Çπ1000" description="Certificate" />
              <RewardCard title="‚Çπ2000" description="T-shirt" />
              <RewardCard title="‚Çπ5000" description="Gift Hamper" />
            </div>
          </>
        ) : (
          <p>Loading...</p>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
