import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Dashboard() {
  const [user, setUser] = useState({ name: '', referralCode: '', amountRaised: 0 });

  useEffect(() => {
    axios.get('http://localhost:5000/api/user')
      .then(res => setUser(res.data))
      .catch(err => console.error('Error fetching user data:', err));
  }, []);

  return (
    <div className="space-y-6">
      <h2 className="text-2xl font-bold text-pink-700">Welcome, {user.name}</h2>

      <div className="grid gap-6 md:grid-cols-2">
        <div className="p-4 bg-white rounded-lg shadow">
          <p className="text-sm text-gray-500">Referral Code</p>
          <p className="text-lg font-semibold">{user.referralCode}</p>
        </div>
        <div className="p-4 bg-white rounded-lg shadow">
          <p className="text-sm text-gray-500">Total Donations Raised</p>
          <p className="text-lg font-semibold">‚Çπ {user.amountRaised}</p>
        </div>
      </div>

      {/* Optional: Rewards Section */}
      <div className="mt-6">
        <h3 className="text-xl font-semibold mb-2 text-pink-700">Rewards</h3>
        <ul className="grid gap-4 md:grid-cols-2">
          <li className="p-4 bg-pink-100 rounded">üéÅ Bronze Badge - ‚Çπ1000</li>
          <li className="p-4 bg-pink-100 rounded">üèÖ Silver Badge - ‚Çπ3000</li>
          <li className="p-4 bg-pink-100 rounded">ü•á Gold Badge - ‚Çπ5000</li>
        </ul>
      </div>
    </div>
  );
}

export default Dashboard;
